def sum_list(numbers):
    total = 0
    for n in numbers:
        total += n
    return total

class Counter:
    def __init__(self):
        self.count = 0

    def increment(self):
        self.count += 1

def is_palindrome(text):
    cleaned = text.lower().replace(' ', '')
    return cleaned == cleaned[::-1]

def find_max(values):
    max_value = values[0]
    for v in values:
        if v > max_value:
            max_value = v
    return max_value
